import React, { Component } from 'react';
import './App.css';

const data = [
  {
    name: "Winnie",
    cards: [
      "Lorem ipsum dolor.",
    "Ut enim ad minim."
    ]
  },
  {
    name: "Bob",
    cards: [
      "Lorem ipsum dolor.",
    "Ut enim ad minim."
    ]
  },
  {
    name: "Thomas",
    cards: [
      "Lorem ipsum dolor.",
    "Ut enim ad minim."
    ]
  },
  {
    name: "George",
    cards: [
      "Lorem ipsum dolor.",
    "Ut enim ad minim."
    ]
  }
]

class App extends Component {
  constructor() {
    super()
    this.state = {
      data: data
    }
    this.handleClick = this.handleClick.bind(this)
  }

  handleClick(e) {
    console.log(e.target.name)
    let text = prompt("Add a card: ")
    let currentName = e.target.name
    let stateCopy = [...this.state.data]
    let currentPersonIdx = stateCopy.findIndex((person) => person.name === currentName)
    stateCopy[currentPersonIdx].cards.push(text)
    console.log(currentPerson)

  }

  render() {
    return (
      <div className="App">
        {
          this.state.data.map((person, idx) => {
            return (
              <div key={`column-${idx}`} className="column">
                <h1 id={person.name}>{person.name}</h1>
                {
                  person.cards.map((card, idx) => {
                    return (
                      <p key={`card-${idx}`}>{card}</p>
                    )
                  })
                }
                <button onClick={this.handleClick} type="button" name={person.name}>+ Add a card.</button>
              </div>
            )
          })
        }
      </div>
    );
  }
}

export default App;
